inline uint32_t BFF(uint32_t X, const secure_vector<uint32_t>& S)
   {
   return ((S[    get_byte(0, X)]  + S[256+get_byte(1, X)]) ^
            S[512+get_byte(2, X)]) + S[768+get_byte(3, X)];
   }

